const url = inputObject.url;
if (!url) return "No URL provided";

try {
  const response = await fetch(url);
  const html = await response.text();
  const text = html.replace(/<[^>]*>/g, ' ').replace(/\s+/g, ' ').substring(0, 10000);
  return text;
} catch (e) {
  return "Error reading website: " + e.message;
}

// Pulisce l'output dell'AI per estrarre solo il JSON
const output = $input.item.json.output || "";
let json = {};

try {
  const start = output.indexOf('{');
  const end = output.lastIndexOf('}');
  if (start !== -1 && end !== -1) {
    json = JSON.parse(output.substring(start, end + 1));
  }
} catch(e) {
  json = { error: "Failed to parse JSON", raw: output };
}

return { json };
